INSERT INTO `tp_problem` VALUES (1, '什么是锦鲤妈妈', '2019年，公司整合国内外顶级专家大咖资源，筹备锦鲤妈妈项目，以助力女性成长创造价值为导向，秉承为中国妈妈成长赋能的宗旨，从【Career事业独立】、【Attraction个人魅力】、【Relationship家庭关系】、【English国际视野】四方面入手，引导广大中国妈妈蜕变全能女性。公司开发教育社交电商产品，融通线上知识学习与线下实训，打造属于中国妈妈的终身成长与知识创富平台。 ', '1576489251', 1, 1);
INSERT INTO `tp_problem` VALUES (2, '如何解绑手机号', '进入个人中心-绑定电话-更换手机号;', '1576489255', 1, 2);
INSERT INTO `tp_problem` VALUES (3, '是否支持退款', '非常抱歉，由于锦鲤妈妈 全部服务属于虚拟内容商品，一旦购买，暂不支持退款，请您谅解。', '1576489261', 1, 3);
INSERT INTO `tp_problem` VALUES (4, '什么是锦鲤妈妈VIP', '锦鲤妈妈VIP为锦鲤妈妈提供的专属会员服务，享受服务有：MOM CARE唤醒课一年内更新课程免费听，赠送MOM CARE严选课5节，加入锦鲤MOM社群得惊喜好礼，全部课程会员专享价，期限为一年。', '1576489267', 1, 4);
INSERT INTO `tp_problem` VALUES (5, '会员赠送免费严选课如何领取', '购买会员后，可按照提示直接领取免费严选课；若未领取成功，可在个人中心-消息栏查看领取。', '1576489272', 1, 5);
INSERT INTO `tp_problem` VALUES (6, 'VIP到期后如何续费', 'VIP到期后，您将无法继续使用会员相关权益，您可在会员购买页面继续购买新的会员使用权限。', '1576489276', 1, 6);
INSERT INTO `tp_problem` VALUES (7, '如何推荐好友购买会员', '您的好友一旦通过扫码识别您的专属分享码/点击您分享的购买链接，成功购买，便绑定从属关系，之后的购买行为产生的奖金便会计算到您的钱包中，现金提取详见提现说明。', '1576489281', 1, 7);
INSERT INTO `tp_problem` VALUES (8, '与被推广人绑定关系是否有时间限制', '推广人与被推广人关系一经绑定后，为永久关系，不可变动。', '1576489284', 1, 8);
INSERT INTO `tp_problem` VALUES (9, '如何判断好友之间的关系绑定', '当用户B初次打开用户A分享的链接并完成了购买会员行为，用户B即刻绑定为用户\r\nA的从属关系，并且为永久关系。', '1576489289', 1, 9);
INSERT INTO `tp_problem` VALUES (10, '用户之间绑定关系的保护期问题', '会员或者大使，用户A发展用户B时，关系界定，以付费为准。关系保护期为7天，未付费，不绑定关系。7天后，用户即成为自由人。若再付费，就成为公司的直接会员。', '1576489293', 1, 10);
INSERT INTO `tp_problem` VALUES (11, '购买后，在哪里观看', '成功购买课程后，可在&ldquo;个人中心-我的课程&rdquo;查看已购课程并进行学习。', '1576489299', 1, 11);
INSERT INTO `tp_problem` VALUES (12, '会员过期后，还能否观看课程', '截至会员过期日期之前更新的MOM CARE唤醒课，您拥有永久观看权，过期日期之后更新的课程，您将无法观看；会员附赠的MOM CARE严选课，你拥有永久观看权。', '1576489304', 1, 12);
INSERT INTO `tp_problem` VALUES (13, '购买训练营后，如何操作', '用户在训练营板块购买任一线下训练营后，需尽快添加训练营详情介绍页的锦鲤客服微信，添加客服微信geniusmini后，客服需要验证您的下单记录，随后为您安排接下来的事宜。', '1576489308', 1, 13);
INSERT INTO `tp_problem` VALUES (14, '如何提现', '在&ldquo;个人中心-我的钱包-去提现&rdquo;处，可依照提现规则提现，提现规则参照体现页面详情介绍。', '1576489314', 1, 14);
INSERT INTO `tp_problem` VALUES (15, '什么时候可以提现', '用户点击提现申请后，提现申请平台审核通过后，现金将在7个工作日内转入。', '1576489318', 1, 15);
INSERT INTO `tp_problem` VALUES (16, '提现是否可以不进行实名认证', '十分抱歉，锦鲤妈妈根据平台提现规则需要用户进行实名认证后才可申请提现，给您带来不便，请您谅解。', '1576489322', 1, 16);
